import React from "react";
import styles from "./styles.module.css";
import Button from "../Button/button";

import { useState } from "react";
import { animated, useSpring } from "react-spring";
import { Link } from "react-router-dom";
import gif from "../loaderlogo.gif";

const Form = (props) => {
    const [data, setData] = useState({
      name: "",
      description: "",
      date: "",
      confirmPassword: "",
      phoneNo: "",
    });
    const [error, setError] = useState({
      error1: "",
      message: "",
    });
    const handleChange = ({ currentTarget: input }) => {
      setData({ ...data, [input.name]: input.value });
    };
    const handleSubmit = async (e) => {
      e.preventDefault();
      const { name, email, password, confirmPassword, phoneNo } = data;
      const res = await fetch("register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          name,
          email,
          password,
          confirmPassword,
          phoneNo,
        }),
      });
      const t = await res.json();
      if (t) {
        if (t.error || t.message) {
          console.log("hello error");
          setError({ error1: t.error, message: t.message });
        }
        if (t.message) {
          window.location.href = "/login";
        }
        //  console.log(error);
      }
    };
  
    const styleprops = useSpring({
      from: {
        opacity: 0,
        transform: "translate(-4000px,-1000px)",
      },
      opacity: 1,
      transform: "translate(0,0)",
    });

  return (
    <>
      <animated.div style={styleprops} className={styles.box}>
        <div className={styles.signup_container}>
          <animated.form style={styleprops} onSubmit={handleSubmit}>
            <h1 className={styles.textlogin}>
              {" "}
              &lt;Credit <span className={styles.st}>/</span>&gt;
            </h1>
            <div className={styles.inputBox}>
              <input
                type="name"
                name="name"
                onChange={handleChange}
                value={data.Name}
                required
                className={styles.input}
              />
              <span>Name</span>
              <br />
              <textarea
                type="description"
                name="description"
                onChange={handleChange}
                value={data.description}
                rows="4"
			    cols="25"
                max="1"
                required
                className={styles.input}
              />
              <span>Description</span>
              <br />
              <input
                type="date"
                name="date"
                onChange={handleChange}
                value={data.date}
                required
                className={styles.input}
              />
              <span>Date</span>
              <br></br>
              {{ error } && (
                <div className={styles.error_msg}>{error.error1}</div>
              )}
              <Button name="Sign Up" />
              <div className={styles.last}>
                <h3>Already have an Account?</h3>
                <Link to="/login">
                  <Button name="Login" />
                </Link>
              </div>
            </div>
          </animated.form>
        </div>
      </animated.div>
    </>
  );
};

export default Form;
