import React from "react";
// importing Link from react-router-dom to navigate to 
// different end points.

import { Link } from "react-router-dom";
import Navbar from "../Navbar";
import Footer from "../Footer";
import Credit from "../Credit"; 
import gif from "../loaderlogo.gif";
import { useState, useEffect } from "react";

import Form from "../Form";
import Notification from "../Notification";


const Home = () => {
  const textnoti=`Sucessfully login `+JSON.parse(localStorage.getItem("loginuser")).name ;
  const [loading, setload] = useState(true);
  useEffect(() => {
    setTimeout(() => {
      setload(false);
    }, 4000);
  }, []);
  var bool =localStorage.getItem("bool");
  return (
    
    <div>
    {loading  ? (
           <center><img src={gif} alt="load"></img></center>
      ) : (
        <div>
  { bool ? <Notification success text={textnoti}error={false} promise={false} /> : null}
    
    <Navbar />

    <Form /></div>

       )} </div>
  );
};
  
export default Home;





