import React from "react";
import styles from "./styles.module.css";
import Button from "../Button/button";
import login from "./login.png";
import { useState } from "react";
import { animated, useSpring } from "react-spring";
import { Link } from "react-router-dom";
import gif from "../loaderlogo.gif";

const SignUp = (props) => {
  const [Data, setData] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
    phoneNo: "",
  });
  const [error, setError] = useState({
    error1: "",
    message: "",
  });
  const handleChange = ({ currentTarget: input }) => {
    setData({ ...Data, [input.name]: input.value });
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    const { name, email, password, confirmPassword, phoneNo } = Data;
    const res = await fetch("register", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name,
        email,
        password,
        confirmPassword,
        phoneNo,
      }),
    });
    const t = await res.json();
    if (t) {
      if (t.error || t.message) {
        console.log("hello error");
        setError({ error1: t.error, message: t.message });
      }
      if (t.message) {
        window.location.href = "/login";
      }
      //  console.log(error);
    }
  };

  const styleprops = useSpring({
    from: {
      opacity: 0,
      transform: "translate(-4000px,-1000px)",
    },
    opacity: 1,
    transform: "translate(0,0)",
  });

  return (
    <>
      <animated.div style={styleprops} className={styles.box}>
          <div className={styles.signup_container}>
            <img src={login} alt="img" className={styles.image}></img>
            <form style={styleprops} onSubmit={handleSubmit}>
              <div className={styles.signup_inner}>
                <h1 className={styles.textlogin}>
                  {" "}
                  &lt;login <span className={styles.st}>/</span>&gt;
                </h1>
                <div className={styles.inputBox}>
                  <input
                    type="email"
                    name="email"
                    onChange={handleChange}
                    value={Data.email}
                    required
                    className={styles.input}
                  />
                  <span>Email</span>

                  <br></br>
                  <input
                    type="password"
                    name="password"
                    onChange={handleChange}
                    value={Data.password}
                    required
                    className={styles.input}
                  />
                  <span>Password</span>
                  <br></br>
                  {error && <div className={styles.error_msg}>{error}</div>}

                  <Button name="Login" />
                  <div className={styles.last}>
                    <h3>Don't have an Account?</h3>
                    <Link to="/login">
                  <Button name="Login" />
                </Link>
                  </div>
                </div>
              </div>
            </form>
          </div>
        </animated.div>
    </>
  );
};

export default SignUp;
