import React from "react";
import styles from "./styles.module.css";
import Button from "../Button/button";
import { useState } from "react";
import { animated, useSpring } from "react-spring";
import { Link } from "react-router-dom";
import gif from "../loaderlogo.gif";

const Form = (props) => {
    const [error, setError] = useState({
      error1: "",
      message: "",
    });
    const [Data, setData] = useState({ amount: "", date: "",  description: ""});
    const [loguser, setLoguser] = useState({
      h: [],
    });
    const [loading, setload] = useState(true);
    const [post, setPost] = useState({
      g: [],
    });
    const [result, setvalue] = useState("");
    const handleChange = ({ currentTarget: input }) => {
      setData({ ...Data, [input.name]: input.value });
    };
    const handleTextarea = ({currentTarget: textarea}) => {
      setData({...Data, [textarea.name] : textarea.value});
    };
    const handleSubmit = async (e) => {
        e.preventDefault();
        const { amount, description, date } = Data;
    
        const namec = { result };
        const loginuser = localStorage.getItem("loginuser");
        const res = await fetch( "/money",
        {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({
              amount,
              namec,
              loginuser,
                  description,
              date
            }),
          },
          []
        );
      };
      const changevalue = (e) => {
        setvalue(e.target.value);
      };
      const fetchcurruser = () => {
        return post.g.find((u) => u.name === x);
      };
    
      useEffect(() => {
        axios.get("http://localhost:8000/data").then(
          (res) => {        
            //const f=res.json();
            // setPost([...post,{[res.data.name]}]);
            // console.log(res.data);
            // console.log(post);
            setPost({ g: res.data });
            let s = fetchcurruser();
            // console.log(s);
            setLoguser({ h: s.credit });
            // console.log(res.data);
          //  console.log(loguser);
        //	console.log(res.description);
            // console.log(res.amount);
            // console.log(post);
          },
          (error) => {
            console.log("error in fetching");
          }
        );
      });
  
    const styleprops = useSpring({
      from: {
        opacity: 0,
        transform: "translate(-4000px,-1000px)",
      },
      opacity: 1,
      transform: "translate(0,0)",
    });

  return (
    <>
      <animated.div style={styleprops} className={styles.box}>
        <div className={styles.signup_container}>
          <animated.form style={styleprops} onSubmit={handleSubmit}>
            <h1 className={styles.textlogin}>
              {" "}
              &lt;Credit <span className={styles.st}>/</span>&gt;
            </h1>
            <div className={styles.inputBox}>
              <input
                type="name"
                name="name"
                onChange={handleChange}
                value={Data.Name}
                required
                className={styles.input}
              />
              <span>Name</span>
              <br />
              <span className={styles.color}>Person: </span>
          <select value={result} onChange={changevalue}>
            <option value="choose">Choose Name</option>

            {post.g.map((add, i) => (
              <option value={add.name} key={i}>
                {add.name}
              </option>
            ))}
          </select>
		  <br />
              <textarea
                type="description"
                name="description"
                onChange={handleChange}
                value={Data.description}
                rows="8"
			    cols="25"
                max="1"
                required
                className={styles.input}
              />
              <span>Description</span>
              <br />
              <input
                type="date"
                name="date"
                onChange={handleChange}
                value={Data.date}
                required
                className={styles.input}
              />
              <span>Date</span>
              <br></br>
              {{ error } && (
                <div className={styles.error_msg}>{error.error1}</div>
              )}
              <Button name="Sign Up" />
              <div className={styles.last}>
                <h3>Already have an Account?</h3>
                <Link to="/login">
                  <Button name="Login" />
                </Link>
              </div>
            </div>
          </animated.form>
        </div>
      </animated.div>
    </>
  )
};

export default Form;
