import React from 'react';
import styles from "./styles.module.css";
import Button from '../Button/button';
import { NavLink} from 'react-router-dom';
import { useState,useEffect } from "react";
import {animated,useSpring } from 'react-spring';
import axios from "axios";
import select from 'react-select';
import Dropdown from '../Dropdown';
// import Drop from '../Drop';

const Credit = () => {
	const items = [
		{
		  id: 1,
		  value: 'Pulp Fiction',
		},
		{
		  id: 2,
		  value: 'The Prestige',
		},
		{
		  id: 3,
		  value: 'Blade Runner 2049',
		},
	  ];
    const [Data, setData] = useState({ amount: "", name: "" });
	const [loading, setload] = useState(true);
	const [error, setError] = useState("");
	const [post, setPost] = useState([{
		name:"",
		email:""
	}]);
	const [result,setvalue]=useState("");
	const handleChange = ({ currentTarget: input }) => {
		setData({ ...Data, [input.name]: input.value });
	};
	const handleSubmit= async(e) =>{
		e.preventDefault();
    
	const changevalue=(e)=>{
		setvalue(e.name);
		
	};
	
	useEffect(()=>{
		
		axios.get("http://localhost:8080/data")
		  .then(res=>{

		
			//const f=res.json();
				// setPost([...post,{[res.data.name]}]);
				// console.log(res.data);
				// console.log(post);
		  },
		  error=>{console.log("error in fecting");})
		},[])
	
  return (
    <div>
	<div className={styles.contmain}>
	        <span className={styles.color}>amount</span>
         	<input
             	type="number"
				name="amount"
				onChange={handleChange}
				value={Data.amount}
				required
            	className={styles.input}
				min="1"
		    />		
   <select className="form-control" value={1} onChange={changevalue}>
              <option value="">Choose Company Name</option>

        {post.map(company => (
              <option value={company.name} key={company._id} >{company.name}</option>
    
              ))
              }

          </select>
		  <h1>{result}</h1>
  
			{/* <Drop /> */}
	
           {/* <Select options={countrylist} onChange={changevalue}/>
		   <center>
			<h1>{result}</h1>
		   </center> */}
			 
           
</div>

    </div>
  )}}


export default Credit
