import React from "react";
import { useState, useRef ,useEffect} from "react";
import { Line } from "react-chartjs-2";
import styles from "./styles.module.css";
import "chart.js/auto"; // ADD THIS
import axios from "axios";


function LineChart() {
  
  const [line,setline]=useState();
  const ref = useRef();
const [value,setvalue]=useState([{
  g:[]
}])
const [res,setres]=useState();
var r=localStorage.getItem("loginuser");
  r= JSON.parse(r);
  r=r.name;
  const [datay,setdatay]=useState(false);
  var data = {
    labels: ["Jan", "Feb", "Mar", "Apr", "May","june","july","Aug","Spet","oct","Nov","Dec"],
    datasets: [
      {
        label: "Sales for 2020 (M)",
        data: res,//[1,2,2],//creditamount,
        borderColor: ['blue'],
        backgroundColor: ['black'],
        pointBackgroundColor: ['red'],
        pointBorderColor: ['violet']
      },
    ],
    options: {
      animations: {
        tension: {
          duration: 1000,
          easing: 'linear',
          from: 1,
          to: 0,
          loop: true
        }
      },
      scales: {
        y: { // defining min and max so hiding the dataset does not change scale range
          min: 0,
          max: 100000,
        }
      }
    }
  };

var creditamount;
var result;
  useEffect(() => {
    const fetchdata= async()=>{
      try{
        const resu = await fetch("/graph", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            r
          }),
        });
      //  const res = await resu.json();
  const re = await resu.json();
  
    setvalue({ g: re });
  console.log(value.g[0].graph[0]);
  result = value.g[0].graph.map(a => parseInt(a.camount));
      setres(result);
      setline(<Line ref={ref} className={styles.chart} data={data} />);

  console.log(result);
  //var creditamount=[value.g[0].graph[0].camount,value.g[0].graph[1].camount,value.g[0].graph[2].camount,value.g[0].graph[3].camount,value.g[0].graph[4].camount,value.g[0].graph[5].camount,value.g[0].graph[6].camount,value.g[0].graph[7].camount,value.g[0].graph[8].camount,value.g[0].graph[9].camount,value.g[0].graph[10].camount,value.g[0].graph[11].camount];
        console.log(creditamount);
      }
     
      catch(error)
      {
        console.log(error);
      }
    }
     fetchdata();
  },[res]);

 
  console.log(data)
  return (
    <>

      <Line ref={ref} className={styles.chart} data={data} />
      {/* <Line ref={ref} className={styles.chart} data={data} /> */}
    </>
  );
}

export default LineChart;
